mapper.select:
SELECT 

FROM pbs_activitytype a

WHERE a.id = #{id}

SELECT 

FROM pbs_activitytype a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.s_name LIKE 
			'%'||#{sName}||'%'
			'%'+#{sName}+'%'
			concat('%',#{sName},'%')


		AND a.s_flowdefinitionid = #{sFlowdefinitionid}


		AND a.s_group = #{sGroup}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pbs_activitytype a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pbs_activitytype SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
