mapper.select:
SELECT 

FROM ccm_worker_attendance a



WHERE a.id = #{id}

SELECT 

FROM ccm_worker_attendance a




	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type = #{type}


		AND a.goout_type = #{gooutType}


		AND a.leave_type = #{leaveType}


		AND a.workingtime_type = #{workingtimeType}


		AND a.attendance_begin >= #{attendanceBegin}


		AND a.attendance_end <= #{attendanceEnd}


		AND a.days = #{days}


		AND a.address LIKE 
			'%'||#{address}||'%'
			'%'+#{address}+'%'
			concat('%',#{address},'%')


		AND a.cause LIKE 
			'%'||#{cause}||'%'
			'%'+#{cause}+'%'
			concat('%',#{cause},'%')


		AND a.create_by = #{createBy.id}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.remarks LIKE 
			'%'||#{remarks}||'%'
			'%'+#{remarks}+'%'
			concat('%',#{remarks},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ccm_worker_attendance a




	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_worker_attendance SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT SUM(IF(goout_type = 10, days, 0)) AS "chuchai", SUM(IF(goout_type = 20, days, 0)) AS "waichu", SUM(IF(leave_type = 10, days, 0)) AS "bingjia", SUM(IF(leave_type = 20, days, 0)) AS "shijia", SUM(IF(leave_type = 30, days, 0)) AS "nianxiu", SUM(IF(leave_type = 40, days, 0)) AS "tiaoxiu", SUM(IF(a.type = 3, days, 0)) AS "jiaban", b.name AS "createBy.name", c.name AS "createBy.office.name", b.name AS "createByname", c.name AS "officename" FROM ccm_worker_attendance a
WHERE a.del_flag = 0
AND a.attendance_begin >= #{attendanceBegin}
AND a.attendance_end <= #{attendanceEnd}
GROUP BY a.create_by
