mapper.select:
SELECT

FROM ccm_device a

WHERE a.id = #{id}

		SELECT

		FROM ccm_device a


			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.code LIKE
				'%'||#{code}||'%'
				'%'+#{code}+'%'
				concat('%',#{code},'%')


				AND a.name LIKE
				'%'||#{name}||'%'
				'%'+#{name}+'%'
				concat('%',#{name},'%')


				AND a.ip LIKE
				'%'||#{ip}||'%'
				'%'+#{ip}+'%'
				concat('%',#{ip},'%')


				AND a.type_id = #{typeId}


				AND a.type_vidicon = #{typeVidicon}


				AND a.area_id = #{area.id}


				AND a.status = #{status}


				AND a.coordinate = #{coordinate}


                AND  (${more1})




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT

FROM ccm_device a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_device SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE ccm_device SET
coordinate = #{coordinate}
WHERE id = #{id}
