mapper.select:
SELECT 

FROM ccm_wechat_event a
WHERE a.id = #{id}

SELECT 

FROM ccm_wechat_event a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.report_info LIKE 
			'%'||#{reportInfo}||'%'
			'%'+#{reportInfo}+'%'
			concat('%',#{reportInfo},'%')


		AND a.report_time BETWEEN #{beginReportTime} AND #{endReportTime}


		AND a.state = #{state}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ccm_wechat_event a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_wechat_event SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
