mapper.select:
SELECT
FROM ccm_house_petition a LEFT JOIN ccm_people b ON a.people_id = b.id
WHERE a.id = #{id}

SELECT
FROM ccm_house_petition a LEFT JOIN ccm_people b ON a.people_id = b.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.people_id = #{peopleId}
AND b.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND b.ident LIKE '%'||#{ident}||'%' '%'+#{ident}+'%' concat('%',#{ident},'%')
AND b.censu LIKE '%'||#{censu}||'%' '%'+#{censu}+'%' concat('%',#{censu},'%')
AND b.telephone LIKE '%'||#{telephone}||'%' '%'+#{telephone}+'%' concat('%',#{telephone},'%')
AND b.sex = #{sex}
AND a.atte_type = #{atteType}
AND a.petition_type = #{petitionType}
AND a.people_number = #{peopleNumber}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM ccm_house_petition a LEFT JOIN ccm_people b ON a.people_id =
b.id




	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_house_petition SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
