mapper.select:
SELECT
FROM ccm_people b LEFT JOIN ccm_house_release a ON a.people_id = b.id
WHERE a.id = #{id}

SELECT
FROM ccm_people b LEFT JOIN ccm_house_release a ON a.people_id = b.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.people_id = #{peopleId}
AND b.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND b.ident LIKE '%'||#{ident}||'%' '%'+#{ident}+'%' concat('%',#{ident},'%')
AND b.censu LIKE '%'||#{censu}||'%' '%'+#{censu}+'%' concat('%',#{censu},'%')
AND b.telephone LIKE '%'||#{telephone}||'%' '%'+#{telephone}+'%' concat('%',#{telephone},'%')
AND b.sex = #{sex}
AND a.recidivism = #{recidivism}
AND a.orig_cha = #{origCha}
AND a.atte_type = #{atteType}
AND a.rele_date BETWEEN #{beginReleDate} AND #{endReleDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT

FROM ccm_house_release a
LEFT JOIN ccm_house_release a ON a.people_id = b.id




	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_house_release SET
del_flag =
#{DEL_FLAG_DELETE}
WHERE id = #{id}
