mapper.select:
SELECT 

FROM ccm_know_key_job a

WHERE a.id = #{id}

SELECT
FROM ccm_know_key_job a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.time_start BETWEEN #{beginTimeStart} AND #{endTimeStart}
AND a.address LIKE '%'||#{address}||'%' '%'+#{address}+'%' concat('%',#{address},'%')
AND a.abstracts LIKE '%'||#{abstracts}||'%' '%'+#{abstracts}+'%' concat('%',#{abstracts},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM ccm_know_key_job a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_know_key_job SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
