mapper.select:
SELECT 

FROM ccm_line_protect a

WHERE a.id = #{id}

SELECT 

FROM ccm_line_protect a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.line_type = #{lineType}


		AND a.dang_grade = #{dangGrade}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ccm_line_protect a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_line_protect SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
