mapper.select:
SELECT
FROM sys_area a LEFT JOIN ccm_org_area b ON a.id = b.area_id LEFT JOIN sys_area c ON c.id = a.parent_id
WHERE a.id = #{id}

SELECT
FROM sys_area a LEFT JOIN ccm_org_area b ON a.id = b.area_id LEFT JOIN sys_area c ON c.id = a.parent_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.code LIKE '%'||#{code}||'%' '%'+#{code}+'%' concat('%',#{code},'%')
AND a.type = #{type}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM sys_area a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_area SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
