mapper.select:
SELECT 

FROM ccm_log_publicity a

WHERE a.id = #{id}

SELECT 

FROM ccm_log_publicity a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.type = #{type}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ccm_log_publicity a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_log_publicity SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
