mapper.select:
SELECT
FROM ccm_place_scenic a LEFT JOIN ccm_base_place b ON a.base_place_id = b.id
WHERE a.id = #{id}

SELECT
FROM ccm_place_scenic a LEFT JOIN ccm_base_place b ON a.base_place_id = b.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND b.place_name LIKE '%'||#{placeName}||'%' '%'+#{placeName}+'%' concat('%',#{placeName},'%')
AND b.leader_name LIKE '%'||#{leaderName}||'%' '%'+#{leaderName}+'%' concat('%',#{leaderName},'%')
AND b.leader_contact LIKE '%'||#{leaderContact}||'%' '%'+#{leaderContact}+'%' concat('%',#{leaderContact},'%')
AND a.id = #{id}
AND a.type = #{type}
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM ccm_place_scenic a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_place_scenic SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
