mapper.select:
SELECT 

FROM ccm_work_notice a

WHERE a.id = #{id}

SELECT 

FROM ccm_work_notice a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.datas BETWEEN #{beginDatas} AND #{endDatas}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ccm_work_notice a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_work_notice SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
