mapper.select:
SELECT 

FROM bph_alarm_notify a

WHERE a.id = #{id}

SELECT 

FROM bph_alarm_notify a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.receive_user_id = #{receiveUserId}


		AND a.type = #{type}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bph_alarm_notify a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bph_alarm_notify SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
