mapper.select:
SELECT DISTINCT a.id AS "id", u.id AS "userId", a.defualt_device AS "defualtDevice", u.office_id AS "office.id", f.name AS "office.name", f.parent_id AS "office.parent.id", f.parent_ids AS "office.parentIds", u.name AS "name", u.login_name AS "loginName", u.phone AS "phone", u.mobile AS "mobile", a.police_phone_code AS "policePhoneCode", a.interphone_code AS "interPhoneCode", a.action_recorder_code AS "actionRecoderCode", c.param AS "param", o.name AS "company.name", u.company_id AS "company.id" FROM sys_user u LEFT JOIN sys_office o ON o.id = u.company_id LEFT JOIN bph_user_extend a ON u.id = a.user_id AND a.del_flag = #{DEL_FLAG_NORMAL} LEFT JOIN sys_office f ON f.id = u.office_id AND f.del_flag = #{DEL_FLAG_NORMAL} LEFT JOIN ccm_mobile_device c ON c.device_id = a.police_phone_code AND c.del_flag = #{DEL_FLAG_NORMAL}
u.del_flag = #{DEL_FLAG_NORMAL}
and u.company_id LIKE '%'||#{company.id}||'%' '%'+#{company.id}+'%' concat('%',#{company.id},'%')
and u.login_name LIKE '%'||#{loginName}||'%' '%'+#{loginName}+'%' concat('%',#{loginName},'%')
and (f.id = #{office.id} OR f.parent_ids LIKE '%'||#{office.id}||'%' '%'+#{office.id}+'%' concat('%',#{office.id},'%'))
and u.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY u.update_date DESC

mapper.update:
UPDATE bph_user_extend SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
