mapper.select:
SELECT 

FROM act_ut_config a

WHERE a.id = #{id}

SELECT
FROM act_ut_config a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
AND a.process_type = #{processType}
AND a.process_name LIKE '%'||#{processName}||'%' '%'+#{processName}+'%' concat('%',#{processName},'%')
AND a.form_key_name LIKE '%'||#{formKeyName}||'%' '%'+#{formKeyName}+'%' concat('%',#{formKeyName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM act_ut_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE act_ut_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
