mapper.select:
SELECT 

FROM plm_home a

WHERE a.id = #{id}

SELECT 

FROM plm_home a


	a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.user_id =#{user.id}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM plm_home a




	AND a.user_id =#{user.id}

SELECT 

FROM plm_home a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE plm_home SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE plm_home SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE user_id = #{user.id}

UPDATE plm_home SET 
	del_flag = #{DEL_FLAG_NORMAL}
WHERE id = #{id}

UPDATE plm_home SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE type != #{type} and user_id = #{user.id}

UPDATE plm_home SET 
	del_flag = #{DEL_FLAG_NORMAL}
WHERE type = #{type} and user_id = #{user.id}
