mapper.select:
SELECT 

FROM plm_incoming_entry a

WHERE a.id = #{id}

SELECT 

FROM plm_incoming_entry a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.provide = #{provide.id}


		AND a.incoming_date BETWEEN #{beginIncomingDate} AND #{endIncomingDate}


		AND a.incoming_code LIKE 
		'%'||#{incomingCode}||'%'
		'%'+#{incomingCode}+'%'
		concat('%',#{incomingCode},'%')


		AND a.incoming_type = #{incomingType}




			AND a.type = #{type}





						AND a.type IN ('0','1','2')


						AND a.type IN ('3','4','5')


						AND a.type IN ('0','1','2')








		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM plm_incoming_entry a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE plm_incoming_entry SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE plm_incoming_entry SET 
	type = #{type}
WHERE id = #{id}
