mapper.select:
SELECT 

FROM ccm_know_policy a

WHERE a.id = #{id}

SELECT
FROM ccm_know_policy a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.type LIKE '%'||#{type}||'%' '%'+#{type}+'%' concat('%',#{type},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.content LIKE '%'||#{content}||'%' '%'+#{content}+'%' concat('%',#{content},'%')
AND a.lss_no LIKE '%'||#{lssNo}||'%' '%'+#{lssNo}+'%' concat('%',#{lssNo},'%')
AND a.rel_date BETWEEN #{beginPublishDate} AND #{endPublishDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM ccm_know_policy a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_know_policy SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
