mapper.select:
SELECT 

FROM ccm_patrol_unit a

WHERE a.id = #{id}

SELECT 

FROM ccm_patrol_unit a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.departure_time > #{departureTime}


		AND a.missions_id = #{missions.id}


		AND a.user_id = #{user.id}


		AND a.missions_id in

			#{item}





		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ccm_patrol_unit a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_patrol_unit SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
