mapper.select:
SELECT 

FROM pbs_courseinfo a

WHERE a.id = #{id}

SELECT 

FROM pbs_courseinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.s_type = #{sType}


		AND a.s_name LIKE 
			'%'||#{sName}||'%'
			'%'+#{sName}+'%'
			concat('%',#{sName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY  a.i_sort DESC

SELECT 

FROM pbs_courseinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY  a.i_sort DESC

mapper.update:
UPDATE pbs_courseinfo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
