mapper.select:
SELECT 

FROM ccm_message a

WHERE a.id = #{id}

SELECT 

FROM ccm_message a


	a.del_flag = #{DEL_FLAG_NORMAL}


			AND (a.type = '02' OR a.type = '04') 


			AND (a.type = '03' OR a.type = '13' OR a.type = '23') 


			AND a.type = '01'



		AND a.content LIKE 
			'%'||#{content}||'%'
			'%'+#{content}+'%'
			concat('%',#{content},'%')


		AND a.user_id = #{user.id}


		AND a.create_by = #{createBy.id}



		AND a.update_date >= #{beginDates}


		AND a.update_date <= #{endDatas}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ccm_message a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ccm_message SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
