mapper.select:
SELECT

FROM kpi_final_score a

WHERE a.id = #{id}

SELECT

FROM kpi_final_score a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.scheme_id = #{schemeId}


		AND a.kpi_name = #{kpiName}


ORDER BY a.kpi_id

SELECT

FROM kpi_final_score a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT DISTINCT
scheme.id AS "id",
scheme.name AS "name"
FROM
kpi_scheme scheme,
sys_user usr,
sys_office office

	scheme.office_id = office.id
	AND usr.office_id = office.id
	AND
	scheme.user_type = usr.user_type
	AND scheme.del_flag =
	#{DEL_FLAG_NORMAL}
	AND usr.del_flag = #{DEL_FLAG_NORMAL}
	AND
	office.del_flag = #{DEL_FLAG_NORMAL}

		AND usr.user_type = #{user.userType}


		AND usr.NAME = #{user.name}


		AND scheme.id = #{schemeId}


		AND office.id = #{office.id}

SELECT DISTINCT usr.id AS "user.id", usr.name AS "user.name", usr.no AS "user.no", usr.user_type AS "user.userType", office.id AS "office.id", office.name AS "office.name", scheme.id AS "schemeId" FROM kpi_scheme scheme, sys_user usr, sys_office office
scheme.office_id = office.id AND usr.office_id = office.id AND scheme.user_type = usr.user_type AND scheme.del_flag = #{DEL_FLAG_NORMAL} AND usr.del_flag = #{DEL_FLAG_NORMAL} AND office.del_flag = #{DEL_FLAG_NORMAL}
AND usr.user_type = #{user.userType}
AND usr.NAME = #{user.name}
AND scheme.id = #{schemeId}
AND office.id = #{office.id}

SELECT DISTINCT usr.id AS "user.id", usr.name AS "user.name", usr.no AS "user.no", usr.user_type AS "user.userType", office.id AS "office.id", office.name AS "office.name", scheme.id AS "schemeId" FROM kpi_scheme scheme, sys_user usr, sys_office office
scheme.office_id = office.id AND usr.office_id = office.id AND scheme.user_type = usr.user_type AND scheme.del_flag = #{DEL_FLAG_NORMAL} AND usr.del_flag = #{DEL_FLAG_NORMAL} AND office.del_flag = #{DEL_FLAG_NORMAL}
AND scheme.id = #{id}

mapper.update:
UPDATE kpi_final_score SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
